package exercice6a

import (
	"fmt"
)

func Run() {
	fmt.Println("exercice 6a")
	fmt.Println(process(input))
}

func process(text string) string {
	size := 4
	allDifferent := false

	end := size - 1
	for !allDifferent {
		end += 1
		start := end - size

		allDifferent = AllCharDifferent(text[start:end])
	}

	return fmt.Sprintf("%d", end)
}

func AllCharDifferent(word string) bool {
	size := len(word)

	for i := 0; i < size; i += 1 {
		for j := i + 1; j < size; j += 1 {
			if word[i] == word[j] {
				return false
			}
		}
	}

	return true
}

